(190, '512be95e-add0-44d2-9b51-4b52500d678f', 'harnis', '56e041ed-7854-4e77-b0f9-37ed8943fdf5', 'Spesifikasi Bread Crumb Orange Natural', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Spesifikasi Breadcrumbs Orange Natural</strong></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-alpha\">\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Production Flow Diagram Orange Bread</strong></span></span></li>\r\n</ol>\r\n\r\n<table cellspacing=\"0\" class=\"MsoTableGrid\" style=\"border-collapse:collapse; border:none; margin-left:26px; width:665px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:1px solid black; vertical-align:top; width:186px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Proses</strong></span></span></p>\r\n			</td>\r\n			<td colspan=\"5\" style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:1px solid black; vertical-align:top; width:479px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Keterangan</strong></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; vertical-align:top; width:186px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Dough Formula</span></span></p>\r\n			</td>\r\n			<td colspan=\"5\" style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; vertical-align:top; width:479px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Room temperature premiks : 15 &ndash; 22&deg;C</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Dough Formula (spesifikasi dan formulasi terlampir)</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; vertical-align:top; width:186px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Mixing Dough</span></span></p>\r\n			</td>\r\n			<td colspan=\"5\" style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; vertical-align:top; width:479px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Parameter:</strong></span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Step 1:</strong></span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Manual dengan hand mixer atau whisker&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3 menit</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Pastikan formula 1 terlarut sempurna/tercampur rata</span></span></p>\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Step 2:</strong></span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Speed 1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3 menit</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Masukkan formula 2, mixing di mixer</span></span></p>\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Step 3:</strong></span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Speed 2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 8 menit</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Masukkan formula 3, mixing di mixer</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Temperatur adonan : 29 &ndash; 31&deg;C</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; vertical-align:top; width:186px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Dough Cutting</span></span></p>\r\n			</td>\r\n			<td colspan=\"5\" style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; vertical-align:top; width:479px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Berat dough/adonan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 630 &ndash; 670 g/pcs</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; vertical-align:top; width:186px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Sheeting &amp; Moulding</span></span></p>\r\n			</td>\r\n			<td colspan=\"5\" style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; vertical-align:top; width:479px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Masukkan adonan yang sudah dipotong ke sheeting moulding machine</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Temperatur adonan : 29 &ndash; 31&deg;C</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; vertical-align:top; width:186px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Transfer to Baking Cart</span></span></p>\r\n			</td>\r\n			<td colspan=\"5\" style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; vertical-align:top; width:479px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Susun adonan 7 pcs, dilipat menjadi setengahnya, masukkan ke baking cart stainless</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">1 kolom/wadah&nbsp;&nbsp;&nbsp; 7 pcs adonan</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">1 baking cart&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 12 kolom</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; vertical-align:top; width:186px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Fermentasi</span></span></p>\r\n			</td>\r\n			<td colspan=\"5\" style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; vertical-align:top; width:479px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Masukkan baking cart ke ruang fermentadi dengan parameter</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Temperatur&nbsp;&nbsp;&nbsp;&nbsp; 34 &ndash; 36&deg;C</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">RH&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 78 &ndash; 82%</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Durasi waktu&nbsp; 60 &ndash; 70 menit</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; vertical-align:top; width:186px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Electric Baking</span></span></p>\r\n			</td>\r\n			<td colspan=\"5\" style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; vertical-align:top; width:479px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Setting Machine ON</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Time Mesin Jepang&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 12 menit</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; High&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5 menit</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Low&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 7 menit</span></span></p>\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Mesin Lokal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 12 menit</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; High&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;3,6 menit</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Low&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 8,4 menit</span></span></p>\r\n\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Temperatur adonan setelah baking 80 &ndash; 97&deg;C</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; vertical-align:top; width:186px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Cooling Down</span></span></p>\r\n			</td>\r\n			<td colspan=\"5\" style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; vertical-align:top