(225, 'ca9b85c5-e8e7-4bbe-95a6-48955d11b246', 'harnis', '009f8990-f23e-403c-a6a3-97dac3cabd23', 'IK QN 05 Pemeriksaan Timbangan', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Times New Roman,Times,serif\">IK QN 05 PEMERIKSAAN TIMBANGAN</span></strong></span></p>\r\n\r\n<table cellspacing=\"0\" class=\"Table\" style=\"border-collapse:collapse; margin-left:6px; width:372px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:19px; vertical-align:bottom; width:95px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Pelaksana</span></span></span></p>\r\n			</td>\r\n			<td style=\"height:19px; vertical-align:bottom; width:149px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">: QC </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:19px; vertical-align:bottom; width:129px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:19px; vertical-align:bottom; width:95px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Frekuensi</span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:19px; vertical-align:bottom; width:278px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">: Setiap awal shift dan pertengahan shift</span></span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>ALAT :</strong></span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Anak timbangan yang sudah dikalibrasi</span></span></p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>CARA PEMERIKSAAN :</strong></span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Meletakan anak timbangan standar yang bersih dan kering pada timbangan yang diperiksa.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Verifikasi<span dir=\"ltr\" lang=\"IN\"> dilakukan minimal 4 jam sekali atau dua kali</span> <span dir=\"ltr\" lang=\"IN\">dalam satu shift</span>.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Catat kode timbangan, jenis timbangan, waktu&nbsp; pemeriksaan, dan kesesuaian antara pembacaan pada layar timbangan dengan anak timbangan pada <span dir=\"ltr\" lang=\"IN\">FM</span>QN 09: Pemeriksaan Timbangan.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Bila ditemukan penyimpangan&nbsp; segera laporkan ke pihak produksi.</span></span></p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n', '2025-05-27 16:10:59', '2025-05-28 08:43:59'),
(226, '76922084-257a-4568-9f41-cb1db74e2012', 'harnis', '009f8990-f23e-403c-a6a3-97dac3cabd23', 'IK QN 06 Kontaminasi Benda Asing', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>IK QN 06 KONTAMINASI BENDA ASING</strong></span></span></p>\r\n\r\n<table cellspacing=\"0\" class=\"Table\" style=\"border-collapse:collapse; margin-left:6px; width:508px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:27px; vertical-align:bottom; width:91px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Pelaksana</span></span></span></p>\r\n			</td>\r\n			<td style=\"height:27px; vertical-align:bottom; width:284px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">: QC </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:27px; vertical-align:bottom; width:132px\">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:27px; vertical-align:bottom; width:91px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Frekuensi</span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:27px; vertical-align:bottom; width:416px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">: Setiap ditemukan penyimpangan proses produksi</span></span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Lakukan verifikasi kontaminasi benda asing setiap proses produksi (termasuk penerimaan bahan baku, bahan penunjang&nbsp; dan proses <em>rework</em> produk)</span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Verifikasi dilakukan secara <em>visual (</em>kasat mata<em>)</em> apabila menemukan kontaminan, segera pisahkan dan beritahukan ke pihak produksi untuk dilakukan tindakan koreksi.</span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"PT-BR\">Catat hasil pemeriksaan dan tempel bukti kontaminasi pada </span><span dir=\"ltr\" lang=\"IN\">FM</span><span dir=\"ltr\" lang=\"PT-BR\">QN 10: K</span><span dir=\"ltr\" lang=\"IN\">ontaminasi </span>B<span dir=\"ltr\" lang=\"IN\">enda </span>A<span dir=\"ltr\" lang=\"IN\">sing</span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '2025-05-27 16:11:15', '2025-05-28 08:44:47'),
(227, 'bca516d9-a0d2-4c8c-9ee1-1cbc23dc09c2', 'harnis', '009f8990-f23e-403c-a6a3-97dac3cabd23', 'IK QN 07 Pemeriksaan Suhu Ruang', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Times New Roman,Times,serif\">IK QN 07 PEMERIKSAAN SUHU RUANG</span></strong></span></p>\r\n\r\n<table cellspacing=\"0\" class=\"Table\" style=\"border-collapse:collapse; margin-left:6px; width:319px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:20px; vertical-align:bottom; width:75px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Pelaksana</span></span></span></p>\r\n			</td>\r\n			<td style=\"height:20px; vertical-align:bottom; width:131px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">: QC </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:20px; vertical-align:bottom; width:113px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:20px; vertical-align:bottom; width:75px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Frekuensi</span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:20px; vertical-align:bottom; width:244px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">: Setiap satu jam sekali</span></span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>ALAT</strong></span></span></p>\r\n\r\n	<ol>\r\n		<li>\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Thermohygrometer</em></span></span></p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>CARA PEMERIKSAAN</strong></span></span></p>\r\n\r\n	<ol>\r\n		<li>\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Lakukan pembacaan suhu dan rH yang tertera pada alat. Suhu ruangan dan rH yang diukur yaitu :</span></span></p>\r\n\r\n		<ol>\r\n			<li>\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Ruang Produksi</span></span></p>\r\n			</li>\r\n			<li>\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Gudang <em>Warehouse</em> (Gudang <em>Finish Good)</em></span></span></p>\r\n			</li>\r\n		</ol>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Catat suhu dan rH <em>thermohygrometer</em> pada <span dir=\"ltr\" lang=\"IN\">FM</span> QN 11: Pemeriksaan Suhu Ruang</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Verifikasi dilakukan setiap satu jam sekali dengan memastikan suhu sesuai dengan standar meliputi suhu ruang produksi yaitu 20-30&deg;C dan suhu gudang <em>Warehaouse</em> yaitu 28-36&deg;C serta standar kelembaban ruang produksi yaitu 40-70% dan kelembaban Gudang <em>Warehaouse</em> yaitu 50-80%.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Bila ada penyimpangan informasikan kepada pihak <em>engineering</em> supaya ditindaklanjuti.</span></span></p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '2025-05-27 16:11:34', '2025-05-28 08:45:34'),
(228, '76812fbb-4335-41cc-8093-dfc3936ea145', 'harnis', '009f8990-f23e-403c-a6a3-97dac3cabd23', 'IK QN 08 Pemeriksaan Proses Pengemasan', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Times New Roman,Times,serif\">IK QN 08 PEMERIKSAAN PROSES PENGEMASAN</span></strong></span></p>\r\n\r\n<table cellspacing=\"0\" class=\"Table\" style=\"border-collapse:collapse; margin-left:6px; width:606px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:22px; vertical-align:bottom; width:119px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Pelaksana</span></span></span></p>\r\n			</td>\r\n			<td style=\"height:22px; vertical-align:bottom; width:198px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">: QC </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:22px; vertical-align:bottom; width:289px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:22px; vertical-align:bottom; width:119px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Frekuensi</span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:22px; vertical-align:bottom; width:487px\">\r\n			<p style=\"margin-right:-143px\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">: Setiap awal <em>batch</em> dan pertengahan/pergantian <em>batch</em> proses pengemasan</span></span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Pastikan kemasan yang dipakai sesuai dengan produk yang akan dikemas.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Verifikasi kondisi kemasan plastik (ada tidaknya keriput, delaminating, gambar berbayang dll).</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Verifikasi kode produksi dan <em>best before </em>produk pada kemasan plastik.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Ambil produk yang sudah dikemas&nbsp; secara acak minimal 10 <em>pack </em>per batchnya.</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"NO-BOK\">Lakukan pemeriksaan hasil <em>sealing</em> meliputi&nbsp; :</span></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<ul style=\"margin-left:40px\">\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"IN\">Celupkan</span> <span dir=\"ltr\" lang=\"IN\">kemasan plastik kosong (tanpa produk) </span>ke dalam air bila muncul <span dir=\"ltr\" lang=\"IN\">gelembung berarti kemasan bocor</span>.</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Lakukan verifikasi dengan <em>red sealer</em> dengan cara menyemprotkan cairan <em>red sealer</em> pada kemasan yang sudah digunting di bagian tengah kemasan (untuk memeriksa kebocoran <em>sealing</em> atas dan bawah kemasan) dan bagian <em>long seal</em> kemasan.</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Lakukan uji kerekatan/kekuatan <span dir=\"ltr\" lang=\"IN\">a</span><span dir=\"ltr\" lang=\"NO-BOK\">da tidaknya kebocoran hasil <em>sealing</em> dengan menekan atau menarik kemasan pada kedua sisi menggunakan tangan.</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<ol start=\"6\">\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"NO-BOK\">Verifikasi kondisi, jenis, kode produksi dan <em>best before</em> pada <em>carton box</em> yang akan digunakan.</span></span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"NO-BOK\">Verifikasi berat dan isi produk/<em>carton box</em> secara acak minimal 10 pack dalam setiap batch produk </span></span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"NO-BOK\">Jika terjadi ketidaksesuaian, informasikan ke produksi untuk dilakukan pemisahan dan tindakan perbaikan.</span></span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Kemasan yang tidak bisa dilakukan perbaikan langsung dipisahkan dan di <em>reject.</em></span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Verifikasi hasil tindakan koreksi dan penyesuaian yang dilakukan oleh produksi.</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"NO-BOK\">Catat semua hasil pemeriksaan dalam FM QN 07 Pemeriksaan Proses Pengemasan</span></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '2025-05-27 16:11:57', '2025-05-28 08:46:34'),
(229, '704ae138-5467-4179-ad1d-f1bb04f5d179', 'harnis', '009f8990-f23e-403c-a6a3-97dac3cabd23', 'IK QN 09 Pengambilan dan Penanganan Sample Produk', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Times New Roman,Times,serif\">IK QN 09 PENGAMBILAN DAN PENANGANAN SAMPLE PRODUK</span></strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Pelaksana&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : QC</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>PENGAMBILAN SAMPEL</strong></span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Retained Sample </strong></span></span></p>\r\n\r\n		<ol>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Retained sampel</em> diambil sebanyak &plusmn;50 gr<span dir=\"ltr\" lang=\"IN\">am</span>.</span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"FI\">Sampel dikemas menggunakan plastik HD, lalu diikat.</span> </span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Sampel yang diambil diberi nama dan kode sesuai dengan kode peroduksi.</span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Sampel disimpan di ruangan khusus yang bersuhu ruang sampai produk kadaluarsa, simpan dalam karton dan diberi label nama produk, bulan dan tahun produksi.</span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"SV\">Pengambilan sampel didokumentasikan pada FM QN 16.</span></span></span></p>\r\n			</li>\r\n		</ol>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Laboratory Sample Produk</strong></span></span></p>\r\n\r\n		<ol>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Laboratory sampel</em> diambil sebanyak &plusmn;50 gram pada setiap tanggal produk.</span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"FI\">Sampel dikemas menggunakan plastik HD, lalu diikat.</span></span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Sampel yang diambil diberi nama dan kode sesuai dengan kode peroduksi.</span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Sampel dikirim ke laboratorium untuk keperluan analisa.</span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"SV\">Sampel yang dikirim ke laboratorium didokumentasikan pada FM LB 02 </span></span></span></p>\r\n			</li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><span dir=\"ltr\" lang=\"SV\">PENANGANAN&nbsp; SAMPEL</span></strong></span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"SV\">&nbsp;<em>Retained sample</em> digunakan untuk keperluan telusur produk dan lain-lainnya yang di dokumentasikan dalam </span><span dir=\"ltr\" lang=\"IN\">FM</span><span dir=\"ltr\" lang=\"SV\"> Q</span><span dir=\"ltr\" lang=\"IN\">N</span><span dir=\"ltr\" lang=\"SV\"> 1</span><span dir=\"ltr\" lang=\"IN\">6</span><span dir=\"ltr\" lang=\"SV\"> : <em>Retain Sample </em></span><em>N<span dir=\"ltr\" lang=\"IN\">ew Seasoning</span></em></span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"SV\">R<em>etained sample</em> yang sudah melewati batas kadaluarsa, dikumpulkan dan dimusnahkan minimal 1 bulan sekali.</span></span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"SV\">Sample yang sudah kadaluarsa kemudian digabung dengan produk <em>reject</em> produksi dan diserahkan kepihak ketiga yang sudah ditunjuk perusahaan. </span></span></span></p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '2025-05-27 16:12:31', '2025-05-28 08:47:24'),
(230, '4fb87f0b-9811-416c-b805-dedeb6c29f28', 'harnis', '009f8990-f23e-403c-a6a3-97dac3cabd23', 'IK QN 10 Area Pemisahan Produk', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Times New Roman,Times,serif\">IK QN 10 AREA PEMISAHAN PRODUK</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol style=\"list-style-type:upper-alpha\">\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Tujuan</strong></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Untuk memastikan produk yang dihasilkan adalah produk yang aman dan layak dikonsumsi dan bebas dari cemaran virus COVID-19 dengan melakukan pemisahan dan karantina khusus.</span></span></p>\r\n\r\n<ol start=\"2\" style=\"list-style-type:upper-alpha\">\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Penanggung Jawab</strong></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">QC <em>Inspector</em>, <em>Foreman</em> Produksi</span></span></p>\r\n\r\n<ol start=\"3\" style=\"list-style-type:upper-alpha\">\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Instruksi Kerja</strong></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<ol style=\"margin-left:40px\">\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Pastikan produk yang terindikasi dari cemaran virus COVID-19 terpisah dengan produk yang lain. </span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Produk yang mengandung cemaran virus COVID-19 disimpan kedalam rak &ndash; rak khusus yang sudah ditentukan.</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Produk yang sudah dipisahkan tersebut diberikan indentitas berupa informasi : nama produk, tanggal produksi, <em>best before</em> beserta jumlahnya.</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Produk yang sudah dipisahkan tersebut tidak diperbolehkan untuk didistribusikan. Status produk : <em>HOLD</em></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '2025-05-27 16:12:52', '2025-05-28 08:48:06'),
(231, '7e214d32-3a5f-4b4a-a931-e4f635fa7124', 'harnis', '009f8990-f23e-403c-a6a3-97dac3cabd23', 'IK QN 11 Penggunaan APD di Area Produksi', '<p style=\"text-align:center\"><strong><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">IK QN 11 PENGGUNAAN APD DI AREA PRODUKSI</span></span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol style=\"list-style-type:upper-alpha\">\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Tujuan</strong></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Untuk memastikan seluruh karyawan didalam area produksi melaksanakan protokol pencegahan bahaya COVID-19 dengan menggunakan peralatan APD sesuai persyaratan.</span></span></p>\r\n\r\n<ol start=\"2\" style=\"list-style-type:upper-alpha\">\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Penanggung Jawab</strong></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">QC <em>Inspector</em>, <em>Foreman </em>Produksi</span></span></p>\r\n\r\n<ol start=\"3\" style=\"list-style-type:upper-alpha\">\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>InstruksiKerja</strong></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<ol style=\"margin-left:40px\">\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Pastikan seluruh karyawan menggunakan peralatan APD (terdiri dari baju seragam, penutup kepala (ciput, <em>hairnet</em> atau jilbab yang berwarna putih), <em>masker</em>, <em>faceshield</em> dan sepatu <em>boot.</em></span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Pastikan karyawan yang bekerja disetiap area menggunakan peralatan APD lengkap sesuai point 1.</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Khusus untuk penggunaan masker <em>disposable</em> (masker sekali pakai) wajib diganti setiap harinya.</span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '2025-05-27 16:13:18', '2025-05-28 08:49:01'),
(232, 'f02a5a21-08bd-4690-a5bd-c8d7396a1d1b', 'harnis', '009f8990-f23e-403c-a6a3-97dac3cabd23', 'IK QN 12 Pelaksanaan Social Distancing di Area Produksi', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Times New Roman,Times,serif\">IK QN 12 PELAKSANAAN SOCIAL DISTANCING DI AREA PRODUKSI</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol style=\"list-style-type:upper-alpha\">\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Tujuan</strong></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Untuk memastikan seluruh karyawan yang bekerja didalam area produksi melaksanakan protokol pencegahan bahaya COVID-19 dengan melakukan <em>social distancing</em> minimal 1 meter.</span></span></p>\r\n\r\n<ol start=\"2\" style=\"list-style-type:upper-alpha\">\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Penanggung Jawab</strong></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">QC <em>Inspector</em>, <em>Foreman </em>Produksi</span></span></p>\r\n\r\n<ol start=\"3\" style=\"list-style-type:upper-alpha\">\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Instruksi Kerja</strong></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<ol style=\"margin-left:40px\">\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Pastikan pelaksanaan <em>social distancing</em> di area tertentu sudah dilakukan oleh seluruh karyawan<em>.</em></span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Pastikan antar karyawan yang bekerja diarea produksi melakukan <em>social distancing</em> minimal berjarak 1 meter.</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Peralatan atau mesin di dalam ruang produksi ditempatkan berjauhan minimal 1 meter dan dibuatkan <em>line marking</em> antar karyawan bekerja sehingga tidak kontak langsung.</span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '2025-05-27 16:14:16', '2025-05-28 08:49:56'),
(233, 'a4c19ff6-6b0e-452d-af49-c418672bdd78', 'harnis', '009f8990-f23e-403c-a6a3-97dac3cabd23', 'IK QN 13 Penanganan Produk Area Mixing', '<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>IK QN 13 PENANGANAN PRODUK AREA MIXING</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Pelaksana: Operator <em>Ribbon Mixer</em> dan QC <em>Inspector</em></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Frekuensi: Setiap terjadi listrik padam</span></span></span></p>\r\n\r\n<ol>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Menghubungi &nbsp;<em>engineering</em> untuk menanyakan informasi</span><span dir=\"ltr\" lang=\"IN\"><span style=\"color:black\">,</span></span><span style=\"color:black\"> yaitu</span><span dir=\"ltr\" lang=\"IN\"><span style=\"color:black\">:</span></span></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<ul style=\"margin-left:40px\">\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Penyebab listrik padam</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Lama waktu yang dibutuhkan untuk listrik kembali normal</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Kesiapan genset</span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Estimasi menyalakan genset</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Melakukan pencatatan </span><span dir=\"ltr\" lang=\"IN\"><span style=\"color:black\">waktu</span></span> <em><span style=\"color:black\">mixing </span></em><span style=\"color:black\">yang sudah berlangsung</span><span dir=\"ltr\" lang=\"IN\"><span style=\"color:black\">.</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Jika listrik padam lebih dari 4 jam maka pindahkan produk ke dalam plastik HD dengan ukuran 10kg dan simpan pada rak penyimpanan</span><span dir=\"ltr\" lang=\"IN\"><span style=\"color:black\">.</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Bila mixing belum selesai, proses mixing dilanjutkan sesuai standar waktu yang ditentukan setelah listrik kembali normal</span><span dir=\"ltr\" lang=\"IN\"><span style=\"color:black\">,</span></span> </span></span></p>\r\n	</li>\r\n</ol>\r\n', '2025-05-27 16:14:50', '2025-05-28 08:50:46'),
(234, '9e95ed90-1452-4a3c-a782-9ae8510c4042', 'harnis', '009f8990-f23e-403c-a6a3-97dac3cabd23', 'IK QN 14 Pemeriksaan Magnet Trap', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>IK QN 14 PEMERIKSAAN MAGNET TRAP</strong></span></span></p>\r\n\r\n<p style=\"margin-left:28px\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Pelaksana&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : QC</span></span></p>\r\n\r\n<p style=\"margin-left:28px\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Frekuensi&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Setiap pergantian <em>batch</em> produksi</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Membersihkan &nbsp;serbuk logam yang menempel di seluruh permukaan <em>magnet trap.</em></span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Menimbang dan catat jumlah temuan serbuk logam yang menempel pada permukaan <em>magnet trap</em> pada FMQN 20: Verifikasi <em>Magnet Trap.</em></span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Setelah dicatat, kontaminasi serbuk logam bisa langsung di musnahkan. </span></span></p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '2025-05-27 16:15:10', '2025-05-28 08:51:27'),
(235, '42555af6-c27a-45f3-9e5d-c3e3df8c3385', 'harnis', '009f8990-f23e-403c-a6a3-97dac3cabd23', 'IK QN 15 Pemeriksaan Kekuatan Magnet Trap', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Times New Roman,Times,serif\">IK QN 15 PEMERIKSAAN KEKUATAN MAGNET TRAP</span></strong></span></p>\r\n\r\n<p style=\"margin-left:28px\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Pelaksana&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : QC</span></span></p>\r\n\r\n<p style=\"margin-left:28px\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Frekuensi&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Sebulan Sekali</span></span></p>\r\n\r\n<p style=\"margin-left:28px\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Hidupkan alat <em>gaussmeter</em>, lalu menempelkan sensor dipermukaan magnet <em>trap </em>dengan jarak 3-5 cm.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Catat hasil pengukuran yang muncul di layar, kemudian dokumentasikan pada FMQN 21: Pemeriksaan Kekuatan <em>Magnet Trap.</em></span></span></p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '2025-05-27 16:15:36', '2025-05-28 08:52:13'),
(236, '77d7256f-525f-43c5-adb8-0c9fbf8b5d18', 'harnis', '009f8990-f23e-403c-a6a3-97dac3cabd23', 'IK QN 17 Penggunaan O2 CO2 Analyzer', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Times New Roman,Times,serif\">IK QN 17 PENGGUNAAN O2 CO2 ANALYZER</span></strong></span></p>\r\n\r\n<table cellspacing=\"0\" class=\"Table\" style=\"border-collapse:collapse; margin-left:6px; width:319px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:20px; vertical-align:bottom; width:75px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Pelaksana</span></span></span></p>\r\n			</td>\r\n			<td style=\"height:20px; vertical-align:bottom; width:131px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">: QC </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:20px; vertical-align:bottom; width:113px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:20px; vertical-align:bottom; width:75px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Frekuensi</span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:20px; vertical-align:bottom; width:244px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">: Setiap satu bulan sekali</span></span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Peralatan</strong></span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>O<sub>2</sub> CO<sub>2</sub> Analyzer</em></span></span></p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Prosedur</strong></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:24px; text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Sebelum memulai pengujian, lakukan kalibrasi alat sebagai berikut:</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Menyambungkan adaptor ke sumber arus, tunggu sampai <em>heater </em>panas selama 1 jam.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Memputar tombol 1 klik ke menu pengukuran, tekan tombol untuk memulai.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Untuk buang udara, gunakan menu <em>clean vacuum</em> dan tekan tombol untuk memulai.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Kembali ke menu pengukuran, kemudian &nbsp;tahan tombol selama 5 detik untuk masuk ke menu kalibrasi. Untuk kalibrasi udara segar, gunakan menu <em>fresh air</em> dan letakkan <em>probe</em> di luar.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Untuk kalibrasi CO<sub>2</sub>, <span dir=\"ltr\" lang=\"IN\">masukkan</span> sedikit<span dir=\"ltr\" lang=\"IN\"> udara luar </span>&nbsp;pada plastik kemudian masukkan <em>probe</em> ke dalam plastik, tekan tombol untuk memulai.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Pilih menu <em>exit</em> untuk kembali ke menu utama.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Buang udara dan alat siap digunakan untuk pengujian.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Memasukkan <em>probe</em> pada objek yang akan diuji. Pastikan <em>probe </em>tidak mengenai produk melainkan hanya mengenai udara di dalam kemasan.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Pilih menu pengukuran, tekan tombol untuk memulai.</span></span></p>\r\n		</li>\r\n		<li>\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Setelah mendapatkan angka, catat hasilnya lalu geser ke menu <em>clean vacuum </em>untuk membuang udara.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Lakukan langkah 2.8 &ndash; 2.10 untuk menguji objek yang berbeda.</span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Jika pengukuran sudah selesai, cabut sambungan adaptor dari sumber listrik untuk mematikan alat.</span></span></p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n', '2025-05-27 16:16:10', '2025-05-28 13:34:21'),
(237, 'ac1e9d88-cc01-469a-90ac-563c4cf6b120', 'harnis', '009f8990-f23e-403c-a6a3-97dac3cabd23', 'IK QN 19 Pengukuran % Nitrogen pada Kemasan Tepung Bumbu', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Times New Roman,Times,serif\">IK QN 19 PENGUKURAN % NITROGEN PADA KEMASAN TEPUNG BUMBU</span></strong></span></p>\r\n\r\n<table cellspacing=\"0\" class=\"Table\" style=\"border-collapse:collapse; margin-left:6px; width:319px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:20px; vertical-align:bottom; width:75px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Pelaksana</span></span></span></p>\r\n			</td>\r\n			<td style=\"height:20px; vertical-align:bottom; width:131px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">: QC </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:20px; vertical-align:bottom; width:113px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:20px; vertical-align:bottom; width:75px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Frekuensi</span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:20px; vertical-align:bottom; width:244px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">: Setiap satu bulan sekali</span></span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol style=\"list-style-type:upper-alpha\">\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>PENGAMBILAN SAMPEL</strong></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<ol style=\"margin-left:40px\">\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Ambil 3 pcs tepung bumbu setiap pergantian batch.</span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<ol start=\"2\" style=\"list-style-type:upper-alpha\">\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>PENGGUNAAN ALAT O2/CO2 ANALYZER</strong></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<ol style=\"margin-left:40px\">\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Siapkan alat O2/CO2 <em>Analyzer.</em></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Kalibrasi alat sebelum melakukan pengujian.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Tekan tombol untuk memulai.</span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<ol start=\"3\" style=\"list-style-type:upper-alpha\">\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>PENGECEKAN KADAR NITROGEN MENGGUNAKAN O2/CO2 ANALYZER</strong></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<ol style=\"margin-left:40px\">\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Masukkan <em>probe</em> pada objek yang akan diuji. Pastikan <em>probe </em>tidak mengenai produk melainkan hanya mengenai udara dalam kemasan saja.</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Pilih menu pengukuran, tekan tombol untuk memulai.</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Setelah mendapatkan angka, catat hasilnya.</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Geser ke menu <em>clean vacuum </em>untuk membuang udara.</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Hitung % nitrogen dengan rumus 100 - (% CO2 + % O2).</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Lakukan langkah 1-5 untuk menguji objek yang berbeda.</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Apabila pengukuran sudah selesai, cabut sambungan adaptor dari sumber listrik untuk mematikan alat.</span></span></p>\r\n	</li>\r\n</ol>\r\n', '2025-05-27 16:16:44', '2025-05-28 08:54:16');
