(248, '488cf005-464c-4c7f-8da2-c1a20dab5de9', 'harnis', '795ec665-153d-48cb-8db4-9d48d969c889', 'IK QB 08 Pemeriksaan Proses Pengemasan', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><strong><span style=\"font-family:Times New Roman,Times,serif\">IK QB 08 Pemeriksaan Proses Pengemasan</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table cellspacing=\"0\" class=\"Table\" style=\"border-collapse:collapse; margin-left:6px; width:322px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:20px; vertical-align:bottom; width:78px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Pelaksana</span></span></span></p>\r\n			</td>\r\n			<td style=\"height:20px; vertical-align:bottom; width:131px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">: QC </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:20px; vertical-align:bottom; width:113px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:20px; vertical-align:bottom; width:78px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Frekuensi</span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:20px; vertical-align:bottom; width:244px\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">: Setiap pergantian batch</span></span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Verifikasi kesesuaian jenis produk yang akan dikemas dengan jenis kemasan yang akan digunakan </span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Periksa kondisi kemasan plastik (ada tidaknya keriput, delaminating, gambar berbayang dll)</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Verifikasi kode produksi dan <em>best before </em>produk pada kemasan plastik</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Mengambil produk yang telah dikemas secara acak minimal 10 pack dalam setiap batch produk</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"NO-BOK\">Verifikasi hasil <em>sealing</em> meliputi&nbsp; :</span></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Mencelupkan&nbsp; <span dir=\"ltr\" lang=\"IN\">kemasan kosong (tanpa produk) jika terdapat gelembung berarti kemasan bocor</span></span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Menginjak<span dir=\"ltr\" lang=\"IN\"> kemasan kosong jika sobek dibagian sealing berarti laminasi hasil sealing kurang</span> bagus</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Verifikasi uji kerekatan/kekuatan <span dir=\"ltr\" lang=\"IN\">a</span><span dir=\"ltr\" lang=\"NO-BOK\">da tidaknya kebocoran hasil <em>sealing</em> dengan menekan atau menarik kemasan pada kedua sisi menggunakan tangan.</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<ol start=\"6\">\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"NO-BOK\">Verifikasi kondisi, jenis, kode produksi dan best before dari karton box yang akan digunakan.</span></span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"NO-BOK\">Verifikasi berat dan isi produk/karton box inner dan outer secara acak minimal 10 pack dalam setiap batch produk </span></span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"NO-BOK\">Jika terjadi ketidaksesuaian, informasikan ke produksi untuk dilakukan pemisahan dan tindakan perbaikan.</span></span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Kemasan yang tidak bisa untuk dilakukan tindakan perbaikan dipisahkan dan di <em>reject.</em></span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Verifikasi hasil tindakan koreksi dan penyesuaian yang dilakukan oleh produksi.</span></span></p>\r\n	</li>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"NO-BOK\">Catat semua hasil pemeriksaan dalam FM QB 08</span></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '2025-05-27 16:22:08', '2025-05-27 16:49:20'),
(249, 'a2bbac6e-f686-40e5-b5dd-57b667cf396e', 'harnis', '795ec665-153d-48cb-8db4-9d48d969c889', 'IK QB 09 Pengambilan dan Penanganan Sample Produk', '<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>IK QB 09 Pengambilan dan Penanganan Sample Produk</strong></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>PENGAMBILAN SAMPLE</strong></span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Retained Sample </strong></span></span></p>\r\n\r\n		<ol>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Mengambil&nbsp; <em>retained sample</em> sebanyak 50 gr pada setiap batch produk, diambil secara&nbsp; acak&nbsp; dari produk yang telah melewati proses pengayakan di ruang packing.</span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"FI\">Sample dikemas menggunakan plastik HD, kemudian kemasan diikat</span></span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Setiap kemasan sample diberi kode sesuai dengan kode produksi dan kadar air (%).</span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Menyimpan sample di ruangan<em> </em>khusus yang bersuhu ruang sampai masa kadaluarsanya, menggunakan plastic berukuran besar dan diberi label bulan dan tahun produksi.</span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"SV\">Pengambilan sample didokumentasikan di dalam FM Q</span><span dir=\"ltr\" lang=\"IN\">B</span><span dir=\"ltr\" lang=\"SV\"> 12.</span></span></span></p>\r\n			</li>\r\n		</ol>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Laboratory Sample</strong></span></span></p>\r\n\r\n		<ol>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Mengambil <em>laboratory sample</em> sebanyak &plusmn;200 gr pada setiap tanggal<em> </em>produk, diambil secara&nbsp; acak&nbsp; dari produk yang ada di ruang packing.</span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"FI\">Sample dikemas menggunakan plastik HD, kemudian mengikat kemasan </span></span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Setiap kemasan sample diberi kode sesuai dengan kode produksi.</span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Sample dikirim ke laboratorium untuk keperluan analisa</span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Sample yang dikirim ke laboratorium <span dir=\"ltr\" lang=\"SV\">didokumentasikan di dalam FM LB 01.</span></span></span></p>\r\n			</li>\r\n		</ol>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><span dir=\"ltr\" lang=\"SV\">Laboratory Sample air</span></strong></span></span></p>\r\n\r\n		<ol>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"SV\">Laboratory sample air diambil sebanyak </span>&plusmn;50 ml pada beberapa titik sampling yaitu (Air PDAM, Air reservoir utsms, Air Kran, Air Chiller) dan untuk pengambilan air limbah sisa hasil cleaning setiap satu minggu sekali.</span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"FI\">Sample dikemas menggunakan plastik HD, kemudian ikat kemasan dengan cara mengikatnya</span></span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Setiap kemasan sample diberi kode sesuai dengan tanggal pengambilan.</span></span></p>\r\n			</li>\r\n			<li style=\"text-align:justify\">\r\n			<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Sample dikirim ke laboratorium untuk keperluan analisa</span></span></p>\r\n			</li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:24px; text-align:justify\">&nbsp;</p>\r\n\r\n<ol start=\"2\">\r\n	<li style=\"text-align:justify\">\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><span dir=\"ltr\" lang=\"SV\">PENANGANAN&nbsp; SAMPLE</span></strong></span></span></p>\r\n\r\n	<ol>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"SV\">&nbsp;<em>Retained sample</em> digunakan untuk keperluan telusur produk dan lain-lainnya yang di dokumentasikan dalam FM QB 12</span></span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"SV\">R<em>etained sample</em> yang sudah kadaluarsa, dikumpulkan untuk dimusnahkan setiap 1 bulan sekali.</span></span></span></p>\r\n		</li>\r\n		<li style=\"text-align:justify\">\r\n		<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><span dir=\"ltr\" lang=\"SV\">Pemusnahan sample dilakukan dengan cara dihancurkan terlebih dahulu dan ditimbun dalam tanah.</span></span></span></p>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n', '2025-05-27 16:22:38', '2025-05-27 16:50:17');
